import React, { useState } from 'react';
import { Form, Input, Button } from 'antd';
import axios from 'axios';
const { TextArea } = Input;

class CustomForm extends React.Component {

  handleFormSubmit = (event, requestType, articleID)=>{
    const title = event.target.elements.title.value;
    const content = event.target.elements.content.value;
    console.log(title,content);

    switch(requestType){
      case 'post': return axios.post('http://127.0.0.1:8000/api/', {
        title: title,
        content: content
      })
      .then(res=>console.log(res))
      .catch(err=>console.log(err));

      case 'put': return axios.put(`http://127.0.0.1:8000/api/${articleID}/`, {
        title: title,
        content: content
      })
      .then(res=>console.log(res))
      .catch(err=>console.log(err));
    }
  }

render(){
  return (
      <div>
        <form onSubmit={(event)=>this.handleFormSubmit(event,
          this.props.requestType,
          this.props.articleID)}>
          <Form.Item label="title">
            <Input name="title" placeholder="Put a title here"/>
          </Form.Item>
          <Form.Item label="content">
          {
            this.props.articleID ? (
              <TextArea rows={9} name="content" placeholder="write some content here" value={"this is "} />

            ):
              <TextArea rows={9} name="content" placeholder="write some content here" value="this is to post"/>
          }
          </Form.Item>
          <Form.Item>
            <button type="submit">Submit</button>
          </Form.Item>
        </form>
      </div>
    );
  }
}

export default CustomForm;
